$(function(){$("#login-form").validate({rules:{id:{required:!0,minlength:4,maxlength:16},pw:{required:!0,minlength:6,maxlength:32}},submitHandler:function(e){$.ajax({url:e.action,type:e.method,data:$(e).serialize(),success:function(e){document.location.href="/main.html"},error:function(e,n){console.log("error"),alert(e.responseJSON.message)}})},debug:!1})});